let script_hex : Hex.t =
  `Hex
    ""

let script_bytes : Bytes.t option = Hex.to_bytes script_hex

let script_opt : Script_repr.expr option =
  Option.bind
    script_bytes
    (Data_encoding.Binary.of_bytes_opt Script_repr.expr_encoding)

let script : Script_repr.expr =
  Option.value_f ~default:(fun () -> assert false) script_opt
